        public static void f1(string s)
        {
            s = s.Replace(",", "");
            s = s.Replace(".", "");
            s = s.Replace(":", "");
            s = s.Replace("?", "");
            s = s.Replace("-", "");
            s = s.Replace("!", "");
            s = s.Replace("(", "");
            s = s.Replace(")", "");
            string[] words = s.Split(' ');
            words = words.Distinct().ToArray();
            for (int i = 0; i < words.Length; i++)
            {
                if (words[i] != "" && ok(words[i]))
                
                File.AppendAllText(@"D:\ee.txt", words[i] + Environment.NewLine);
            }
        }
      static  bool ok(string s)
        {
            for (int i = 0; i < s.Length; i++)
            {
                if (s[i] < 'א' || s[i]>'ת')
                    return false;
            }
            return true;
        }
        public static void RemoveDuplicateWords()
        {
            // Read all lines from the file
            string[] lines = File.ReadAllLines(@"D:\ee.txt");

            // Split lines into words, remove duplicates, and sort alphabetically
            var words = lines.SelectMany(line => line.Split('\n'))
                             .Distinct()
                             .OrderBy(word => word);

            // Write unique words back to the file, each on a separate line
            File.WriteAllLines(@"D:\ee.txt", words);
        }
        public static void WriteToJsonFile(string filePath, List<Function> data)
        {

            // Write the JSON array to the specified file
            using (StreamWriter file = File.CreateText(filePath))
            {
                Newtonsoft.Json.JsonSerializer serializer = new Newtonsoft.Json.JsonSerializer();
                serializer.Serialize(file, data);
            }
        }
